#!/usr/bin/env bash
set -euo pipefail

REPO_URL="${CURSOR_CONFIG_REPO_URL:-https://github.com/DaikoAI/.cursor.git}"
BRANCH="${CURSOR_CONFIG_BRANCH:-main}"
PREFIX="${CURSOR_CONFIG_PREFIX:-.cursor}"

# Only sync these directories (exclude README, package.json, scripts, etc.)
SYNC_DIRS=("commands" "rules")

TMP_DIR=""

# ANSI color codes
RESET="\033[0m"
BOLD="\033[1m"
DIM="\033[2m"
GREEN="\033[32m"
BLUE="\033[34m"
CYAN="\033[36m"
YELLOW="\033[33m"
RED="\033[31m"
MAGENTA="\033[35m"

SPINNER_PID=""

cleanup_tmp_dir() {
  if [[ -n "${TMP_DIR:-}" && -d "${TMP_DIR:-}" ]]; then
    rm -rf -- "$TMP_DIR"
  fi
}

stop_spinner() {
  if [[ -n "${SPINNER_PID:-}" ]]; then
    kill "${SPINNER_PID}" 2>/dev/null || true
    wait "${SPINNER_PID}" 2>/dev/null || true
    SPINNER_PID=""
  fi
}

cleanup_all() {
  stop_spinner
  cleanup_tmp_dir
}

trap cleanup_all EXIT

start_spinner() {
  local msg="${1:-Loading}"

  # Only show spinner on TTY
  if [[ ! -t 2 ]]; then
    echo -e "${CYAN}${BOLD}→${RESET} ${BOLD}${msg}...${RESET}" >&2
    return 0
  fi

  # Braille spinner frames (smooth rotation)
  (
    local frames=('⠋' '⠙' '⠹' '⠸' '⠼' '⠴' '⠦' '⠧' '⠇' '⠏')
    local i=0
    while true; do
      printf "\r${CYAN}${BOLD}${frames[i]}${RESET} ${BOLD}${msg}...${RESET}" >&2
      i=$(( (i + 1) % ${#frames[@]} ))
      sleep 0.08
    done
  ) &
  SPINNER_PID=$!
}

finish_spinner() {
  local msg="${1:-Done}"
  stop_spinner
  if [[ -t 2 ]]; then
    printf "\r${GREEN}${BOLD}✓${RESET} ${BOLD}${msg}${RESET}                    \n" >&2
  else
    echo -e "${GREEN}${BOLD}✓${RESET} ${BOLD}${msg}${RESET}" >&2
  fi
}

print_usage() {
  cat <<'EOF'
Usage:
  cursor-config.sh [install|update]

Environment variables:
  CURSOR_CONFIG_REPO_URL   (default: https://github.com/DaikoAI/.cursor.git)
  CURSOR_CONFIG_BRANCH     (default: main)
  CURSOR_CONFIG_PREFIX     (default: .cursor)

Notes:
  - install/update behave the same (sync latest).
  - Only syncs: commands/, rules/
  - Replaces managed directories completely (deletions upstream are applied).
  - Your project-specific additions outside managed directories are preserved.
EOF
}

print_daiko_banner() {
  cat <<'EOF'

    ██████╗  █████╗ ██╗██╗  ██╗ ██████╗
    ██╔══██╗██╔══██╗██║██║ ██╔╝██╔═══██╗
    ██║  ██║███████║██║█████╔╝ ██║   ██║
    ██║  ██║██╔══██║██║██╔═██╗ ██║   ██║
    ██████╔╝██║  ██║██║██║  ██╗╚██████╔╝
    ╚═════╝ ╚═╝  ╚═╝╚═╝╚═╝  ╚═╝ ╚═════╝

EOF
}

is_submodule_target() {
  if [[ ! -f .gitmodules ]]; then
    return 1
  fi

  while IFS= read -r line; do
    local value
    value="${line#* }"
    if [[ "$value" == "$PREFIX" ]]; then
      return 0
    fi
  done < <(git config -f .gitmodules --get-regexp '^submodule\..*\.path$' 2>/dev/null || true)

  return 1
}

sync_now() {
  start_spinner "Fetching cursor configuration"
  TMP_DIR="$(mktemp -d)"
  git clone --depth 1 --branch "$BRANCH" "$REPO_URL" "$TMP_DIR" >/dev/null 2>&1
  finish_spinner "Fetched"

  mkdir -p "$PREFIX"

  echo -e "${CYAN}${BOLD}→${RESET} ${BOLD}Syncing into ${PREFIX}/...${RESET}" >&2

  local synced=0

  # Sync only specified directories (replace completely)
  for dir in "${SYNC_DIRS[@]}"; do
    if [[ -d "$TMP_DIR/$dir" ]]; then
      echo -e "  ${MAGENTA}•${RESET} ${DIM}$dir/${RESET}" >&2

      # Remove existing directory completely
      rm -rf "$PREFIX/$dir"

      # Copy fresh directory
      mkdir -p "$PREFIX/$dir"
      cp -R "$TMP_DIR/$dir/"* "$PREFIX/$dir/" 2>/dev/null || true

      # Count files
      local count
      count=$(find "$TMP_DIR/$dir" -type f 2>/dev/null | wc -l | tr -d ' ')
      synced=$((synced + count))
    fi
  done

  echo "" >&2
  echo -e "${GREEN}${BOLD}✓${RESET} ${BOLD}Sync completed!${RESET}" >&2
  echo -e "${DIM}  Synced ${synced} file(s)${RESET}" >&2

  if git rev-parse --is-inside-work-tree >/dev/null 2>&1; then
    echo "" >&2
    echo -e "${YELLOW}${BOLD}→${RESET} ${DIM}Next steps:${RESET}" >&2
    echo -e "  ${DIM}git add \"$PREFIX\" && git commit -m \"chore: sync cursor config\"${RESET}" >&2
  fi

  echo "" >&2
  echo -e "${MAGENTA}${BOLD}" >&2
  print_daiko_banner >&2
  echo -e "${RESET}" >&2

  # Onboarding message
